Ext.define('TabUserInformation.view.Window.JobWindowViewController', {
    extend: 'Ext.app.ViewController',
    alias: 'controller.windowjobwindow',

    onViewItemDblClick: function(dataview, record, item, index, e, eOpts) {
        var data = record.data.text;
        var popup = null;
        if(record.data.leaf){
            switch(data.substring(0,2)){
                case 'VS':
                popup = Ext.create('widget.windowvisticallingwindow');
                break;
                case 'IF':
                popup = Ext.create('widget.windowinformationforindicationwindow');
                break;
                case 'ID':
                popup = Ext.create('widget.windowindicationforequipmentwindow');
                break;
                default:
                break;
            }
        }
        popup.show();
    },

    onViewItemContextMenu: function(dataview, record, item, index, e, eOpts) {
        console.log(record);
    }

});
