Ext.define('TabUserInformation.view.Guarantor.GuarantorWindowViewController', {
    extend: 'Ext.app.ViewController',
    alias: 'controller.guarantorguarantorwindow',

    onComboboxBotCollTypeDescChange: function(field, newValue, oldValue, eOpts) {
        var form = this.getView().down('form').getForm(),
            store = Ext.getStore('CommonData.BOTSubcollaterals');

        if(newValue) {
            form.findField('BotCollSubTypeDesc').enable();

            store.load({
                params: {
                    id: newValue
                },
                callback: function(records, operation, success) {
                    if(oldValue){
                        form.findField('BotCollSubTypeDesc').setValue(null);
                    }
                }
            })
        }else {
            form.findField('BotCollSubTypeDesc').disable();
            form.findField('BotCollSubTypeDesc').setValue(null);
        }
    },

    onButtonSaveClick: function (button, e, eOpts) {
        var view = this.getView();

        Ext.MessageBox.confirm('Confirm', 'Confirm Save?', function (msg) {
            if (msg === 'yes') {
                TabUserInformation.controller.WindowController.fnSaveChildSession(view);
            }
        });
    },

});
