Ext.define('TabUserInformation.view.Job.Application.Tab.DocumentViewController', {
    extend: 'Ext.app.ViewController',
    alias: 'controller.jobapplicationtabdocument',

    onButtonUploadClick: function(button, e, eOpts) {
        var filefield = this.getView().down('filefield'),
            form = this.getView().getForm();

        if (form.isValid()) {
            form.submit({
                url: 'Home/FileUpload',
                success: function (form, action) {
                    Ext.Msg.alert('Success', action.result.message);
                    //             me.intend = "save-success";
                    //             me.close();
                },
                failure: function (form, action) {
                    Ext.Msg.alert('Failed', action.result ? action.result.message : 'No response');
                }
            });
        } else {
            Ext.Msg.alert('Data is not valid!', 'กรุณาเลือกข้อมูลให้ครบถ้วน');
        }
    }

});
