Ext.define('TabUserInformation.view.Job.Application.Window.PurchaseOrderDetailViewController', {
    extend: 'Ext.app.ViewController',
    alias: 'controller.jobapplicationwindowpurchaseorderdetail',

    onButtonSignerClick: function(button, e, eOpts) {
        var view = this.getView(),
            form = view.down('form').getForm();

        var popup = Ext.create('widget.commonsignerpopup', {
            listeners: {
                close: function (panel, eOpts) {
                    var record2 = panel.down('gridpanel').getSelection()[0];
                    if(record2){
                        var name = record2.get('TitleNameEng')+' '+
                        record2.get('FirstNameEng')+' '+
                        record2.get('LastNameEng');

                        form.findField('SignerName').setValue(name);
                    }
                }
            }
        });
        popup.show();
    },

    onButtonAddClick: function(button, e, eOpts) {
        var store = this.getView().down('grid').getStore();
        var record = Ext.create('model.equipmentlist');

        store.add(record);
        this.getView().down('grid').view.refresh();
    },

    onButtonInsertClick: function(button, e, eOpts) {

    },

    onButtonDeleteClick: function(button, e, eOpts) {
        var store = this.getView().down('grid').getStore(),
            record = this.getView().down('grid').getSelection()[0];

        if(record){
            if(record.get('Id') != '0'){
                Ext.MessageBox.confirm('Confirm','Confirm Delete?',
                function(msg){
                    if(msg == 'yes'){
                        store.remove(record);
                    }
                },this);
            }else{
                store.remove(record);
            }
        }

    }

});
