    onButtonNewLineClick: function (button, e, eOpts) {
        var form = this.getView().down('form').getForm(),
            grid = this.getView().down('grid'),
            store = grid.getStore();

        var record = Ext.create('model.equipment', {
            IndicationEquipment: {
                Id: form.findField('Id').getValue()
            }
        });

        store.insert(store.data.length, record);
        grid.getView().focusRow(store.data.length - 1);

        var popup = Ext.create('widget.jobindicationindicationdetail', {
            listeners: {
                beforerender: function (panel, eOpts) {
                    var form = panel.down('form').getForm();

                    if (store.data.length > 0) {
                        form.loadRecord(store.data.items[store.data.length - 2]);
                        form.findField('Id').setValue(null);
                    }
                },
                close: function (panel, eOpts) {
                    store.load();
                    grid.view.refresh();
                }
            }
        });
        popup.show();
    },

