Ext.define('TabUserInformation.view.Job.Information.DetailRequestTransactionViewController', {
    extend: 'Ext.app.ViewController',
    alias: 'controller.jobinformationdetailrequesttransaction',

    fnSave: function () {
        var form = this.getView().down('form').getForm(),
            record = form.getRecord();

        form.updateRecord(record);

        if (form.isValid()) {
            if (record.store.getModifiedRecords().length > 0) {
                record.store.sync({
                    success: function (response) {
                        Ext.MessageBox.alert("Result", "Successful.");
                    },
                    failure: function (response) {
                        Ext.Msg.show({
                            title: 'Error',
                            message: response.responseText,
                            buttons: Ext.Msg.OK,
                            icon: Ext.Msg.ERROR
                        });
                    }
                });
            } else {
                Ext.MessageBox.alert('Warning', 'ไม่พบการเปลี่ยนแปลงข้อมูล');
            }
        } else {
            Ext.MessageBox.alert('Warning', 'กรอกข้อมูลไม่ครบถ้วน');
        }
    },

    onButtonSaveClick: function (button, e, eOpts) {
        var me = this;

        Ext.MessageBox.confirm('Confirm', 'Confirm Save?', function (msg) {
            if (msg === 'yes') {
                me.fnSave();
            }
        });
    },

    onButtonNewIndicationClick: function (button, e, eOpts) {
        var panel = Ext.getCmp('jobjobwindow'),
            grid = panel.down('jobindicationindicationforequipmenttab').down('grid'),
            store = grid.getStore(),
            me = this,
            form = me.getView().down('form').getForm();

        Ext.Msg.show({
            title: 'Save Changes?',
            message: 'คุณต้องการสร้าง New Indication หรือไม่?',
            buttons: Ext.Msg.YESNOCANCEL,
            icon: Ext.Msg.QUESTION,
            fn: function (btn) {
                if (btn === 'yes') {
                    store.add({
                        IndicationId: 'ID' + Ext.Date.format(new Date(), 'Y') + '00002',
                        Year: Ext.Date.format(new Date(), 'Y')
                    });

                    //--close page--//
                    form.findField('save').setValue('Y');
                    panel.down('tabpanel').setActiveTab(2);
                    me.getView().close();
                    Ext.getCmp('jobinformationinformationforindicationwindow').down('form').getForm().findField('save').setValue('Y');
                    Ext.getCmp('jobinformationinformationforindicationwindow').close();
                } else if (btn === 'no') {
                } else {
                }
            }
        });
    },

    onBeforeClose: function (panel, eOpts) {
        var me = this,
            form = panel.down('form').getForm();

        if (panel.closeMe) {
            panel.closeMe = false;
            return true;
        }

        if (form.findField('save').getValue() === 'N') {
            Ext.Msg.show({
                title: 'Save',
                message: 'Save Changes?',
                buttons: Ext.Msg.YESNOCANCEL,
                icon: Ext.Msg.QUESTION,
                width: 300,
                fn: function (btn) {
                    if (btn === 'yes') {
                        Ext.MessageBox.show({
                            title: 'Please wait',
                            msg: 'Saving items...',
                            progressText: 'Saving...',
                            width: 300,
                            progress: true,
                            closable: false,
                        });

                        me.fnSave();

                    } else if (btn === 'no') {
                        panel.closeMe = true;
                        panel.close();
                    }
                }
            });
        }else {
            panel.closeMe = true;
            panel.close();
        }

        return false;
    },

    onClose: function (panel, eOpts) {

    }

});
